/*---------------------------*\
    #variables
\*----------------------------*/

/*|| colors */
:root {
  --Very-Dark-Gray: hsl(0, 0%, 17%);
  --Dark-Gray: hsl(0, 0%, 59%);
  --black: hsl(0, 0%, 0%);
  --white: hsl(0, 0%, 100%);
}

/*|| typography */
@import url("https://fonts.googleapis.com/css2?family=Rubik:wght@400;500;700&display=swap");
$fontsize-textinput: 18px;
$font-primary: "Rubik", sans-serif;

/*|| breakpoints */
$breakpoints: (
  "tablet": 43em,
  "laptop": 62em,
  "desktop": 82em,
);

/*|| transitions */
$primary-transition: all 500ms ease-in-out;
$search-bar-transition: all 500ms cubic-bezier(0, 1.32, 1, -0.3);

