@use "../abstracts" as abs;

/*----------------------------------------*\
  #ip-tracker
\*----------------------------------------*/

.ip-tracker {
  &__header {
    background: url("../assets/images/pattern-bg.png") no-repeat center center/
      cover;
    text-align: center;
  }

  &-wrapper {
    padding-top: 1.7rem;
    position: relative;
    transition: abs.$search-bar-transition;
  }

  &__heading {
    font-size: 1.5rem;
    margin-bottom: 1.85rem;
    color: var(--white);
  }

  &__form {
    display: inline-block;
    width: 100%;
    position: relative;
    margin-bottom: 9.6rem;
    transform-origin: left;
    transition: abs.$search-bar-transition;
    max-width: 34.5rem;
    &--active {
      @extend .ip-tracker__form;
    }
  }

  &__btn {
    transition: abs.$search-bar-transition;
    border: none;
    position: absolute;
    left: 100%;
    top: 0;
    background: url(../assets/images/icon-arrow.svg) no-repeat center, black;
    transform: translateX(-100%);
    width: 3.7rem;
    height: 100%;
    border-top-right-radius: 1rem;
    border-bottom-right-radius: 1rem;

    .fa-search {
      color: var(--white);
      visibility: hidden;
      transform: scale(0);
    }

    &:hover {
      transform-origin: center;
      transform: translateX(-100%) rotate(180deg);
      border-top-right-radius: 0;
      border-bottom-right-radius: 0;
      border-top-left-radius: 1rem;
      border-bottom-left-radius: 1rem;
    }

    &--active {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background: inherit;
      font-size: 3rem;
      transition: abs.$search-bar-transition;
      overflow: hidden;
      .fa-search {
        visibility: visible;
        transform: scale(1);
      }
      &:hover {
        transform: translate(-50%, -50%);
      }
    }
  }

  &__input {
    text-align: left;
    padding: 1.2rem 1.5rem;
    width: 100%;
    font-size: 0.9rem;
    border-radius: 1rem;
    border: none;
    transform: scale(1);
    visibility: visible;
    transform-origin: center;
    transition: abs.$search-bar-transition;

    &--active {
      visibility: hidden;
      transform: scale(0);
    }
  }

  &__info {
    background-color: var(--white);
    position: absolute;
    z-index: 2;
    left: 50%;
    transform: translate(-50%, -45%) scale(1);
    width: 100%;
    min-height: 18.3rem;
    border-radius: 1rem;
    @include abs.flex-container($js-con: space-evenly, $flex-d: column);
    transition: abs.$search-bar-transition;

    &--active {
      @extend .ip-tracker__info;
      width: 0;
      visibility: hidden;
      transform: scale(0);
      border-radius: 10rem;
    }
  }

  &__item {
    &-heading {
      font-size: 0.8rem;
      text-transform: uppercase;
      margin-bottom: 0.3rem;
    }

    &-info {
      font-size: 1.25rem;
      font-weight: 500;
    }
  }

  /*----------------------------------------*\
      #ip-tracker map
  \*----------------------------------------*/

  &__map {
    position: relative;
    height: 32.5rem;
    z-index: 1;
  }

  /*----------------------------------------*\
  #ip-tracker tablet view
\*----------------------------------------*/
  @include abs.respond-to("tablet") {
    &__heading {
      font-size: 1.9rem;
    }

    &__input {
      font-size: 1.1rem;
    }
  }

  /*----------------------------------------*\
  #ip-tracker laptop view
\*----------------------------------------*/

  @include abs.respond-to("laptop") {
    &__form {
      margin-bottom: 8rem;
    }

    &__info {
      transform: translate(-50%, -50%);
      min-height: 10rem;
      flex-direction: row;
    }

    &__info {
      text-align: left;
    }

    &__item {
      flex: 1;
      margin-left: 2rem;
      min-height: 6rem;

      &-heading {
        font-size: 0.8rem;
        text-transform: uppercase;
        margin: 0.1rem 0 0.9rem 0;
        letter-spacing: 0.1rem;
      }

      &-info {
        font-size: 1.6rem;
      }
    }
  }
}
